    function __construct($times = self::RUN_TIMES){

        $this->run($times);
    }

    public function run($times){

        $this->_setData();
        foreach($this->func_arr as $type => $function){
            $str = '';
            //封包
            $stime = microtime(true);
            for($i = 0; $i < $times; $i++){
                $str = call_user_func_array(array($this, $function), array($type));
            }
            $etime                            = microtime(true);
            $pack_time                        = $etime - $stime;
            $this->result[$type]['pack_time'] = $this->_formatTime($pack_time);
            $this->result[$type]['package'] = $this->convert(strlen($str));
            //拆包
            $function = "un{$function}";
            for($i = 0; $i < $times; $i++){
                call_user_func_array(array($this, $function), array($str));
            }
            $unpack_time                        = microtime(true) - $etime;
            $this->result[$type]['unpack_time'] = $this->_formatTime($unpack_time);
        }
        print_r($this->result);
    }

    function msgpack($type){

        $s   = memory_get_usage();
        $str = msgpack_pack($this->data);
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function unmsgpack($str){

        msgpack_unpack($str);
    }

    function gzencode_msgpack_pack($type){

        $s   = memory_get_usage();
        $str = gzencode(msgpack_pack($this->data));
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function ungzencode_msgpack_pack($str){

        return msgpack_unpack(gzdecode($str));
    }

    function gzdeflate_msgpack_pack($type){

        $s   = memory_get_usage();
        $str = gzdeflate(msgpack_pack($this->data));
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function ungzdeflate_msgpack_pack($str){

        return msgpack_unpack(gzinflate($str));
    }

    function json_pack($type){

