            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function unjson_pack($str){

        return json_decode($str);
    }

    function gzencode_json_pack($type){

        $s   = memory_get_usage();
        $str = gzencode(json_encode($this->data));
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function ungzencode_json_pack($str){

        return json_decode(gzdecode($str));
    }

    function gzdeflate_json_pack($type){

        $s   = memory_get_usage();
        $str = gzdeflate(json_encode($this->data));
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
