            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function ungzdeflate_json_pack($str){

        return json_decode(gzinflate($str));
    }

    function gzcompress_msgpack_pack($type){

        $s   = memory_get_usage();
        $str = gzcompress(msgpack_pack($this->data));
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function ungzcompress_msgpack_pack($str){

        return msgpack_unpack(gzuncompress($str));
    }

    function gzencode_serialize_pack($type){

        $s   = memory_get_usage();
        $str = gzencode(serialize($this->data));
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;
    }

    function ungzencode_serialize_pack($str){

        return unserialize(gzdecode($str));
    }

    function serialize_pack($type){

        $s   = memory_get_usage();
        $str = serialize($this->data);
        $e   = memory_get_usage();
        if(isset($this->result[$type]['mem'])){
            $this->result[$type]['mem'] += $e - $s;
        }else{
            $this->result[$type]['mem'] = 0;
        }

        return $str;

    }

    function unserialize_pack($str){

        return unserialize($str);
    }

    function convert($size){

        $unit = array('B', 'K', 'M', 'G', 'T', 'P');

        return @round($size / pow(1024, ($i = floor(log($size, 1024)))), 2) . ' ' . $unit[$i];
    }

    private function _formatTime($time){

        return number_format($time, 7, '.', '');
    }

