/**
 * 测试
 * @param int $count 测试方法执行次数
 */
function run($count){

    $str = getData(30);
    $arr = array(
        'msgpack' => 'msgpack',
        'gzencode msgpack' => 'gzencode_msgpack_pack',
        'gzdeflate msgpack' => 'gzdeflate_msgpack_pack',
        'gzcompress msgpack' => 'gzcompress_msgpack_pack',
        'json_encode' => 'json_pack',
        'gzencode json_encode' => 'gzencode_json_pack',
        'gzdeflate json_encode' => 'gzdeflate_json_pack',
        'gzencode serialize' => 'gzencode_serialize_pack',
        'serialize' => 'serialize_pack',
    );
    foreach($arr as $type => $function){
        $smem  = memory_get_usage();
        $stime = microtime(true);
        for($i = 0; $i < $count; $i++){
            $str_pack = $function($str);
        }
        $etime     = microtime(true);
        $emem      = memory_get_usage();
        $total     = ($etime - $stime) / $count;
        $total_mem = $emem - $smem;
        echo "{$type}\n";
        echo "Compression time: " . number_format($total, 7, '.', '') . "\t";
        $unfunction = "un{$function}";
        $stime      = microtime(true);
        for($i = 0; $i < $count; $i++){
            $unfunction($str_pack);
        }
        $etime = microtime(true);
        $total = ($etime - $stime) / $count;
        echo "Decompression time: " . number_format($total, 7, '.', '') . "\t Package length: " . strlen($str_pack) . "\t Mem: " . strlen($total_mem) . "\n\n";
    }
}

